from longest_palindrome import Solution


def test_ex1():
    s = "abccccdd"
    solution = Solution()
    result = solution.longestPalindrome(s)
    assert result == 7


def test_ex2():
    s = "a"
    solution = Solution()
    result = solution.longestPalindrome(s)
    assert result == 1


def test_ex3():
    s = ""
    solution = Solution()
    result = solution.longestPalindrome(s)
    assert result == 983
